$(document).ready(function(){$("#payment-form").hide();$("#order-summary").hide();$(".online-type").hide();$(".sms-type").hide();$(".btn-promotion").click(function(){var a=$(this).data("pid");var b=$(this).data("type");if(b.indexOf("ONLINE")!==-1){$(".online-type").show()}else{$(".online-type").hide()}if(b.indexOf("SMS")!==-1){$(".sms-type").show()}else{$(".sms-type").hide()}$("#order-summary").fadeOut(100);$("#payment-form").fadeIn(1000);$(".btn-payment").click(function(){var c=$(this).data("payment");$.countAmount(a,c)})})});$.countAmount=function(b,a){$.ajax({type:"POST",data:{pid:b,payment:a},url:"/ajax/objects/order-amount/",success:function(d){if(typeof d=="string"){var c=$.parseJSON(d);console.log(c);if(typeof c=="object"){if(c.amount){$(".summary-text").empty().append(c.text);$(".summary-amount").html(c.amount);$("#order-summary").fadeIn(400);$("#order-summary-error").hide()}else{$("#order-summary").hide();$("#order-summary-error").show()}}}},complete:function(){$("#order-summary .form-hidden").html('<input type="hidden" name="pid" value="'+b+'"/><input type="hidden" name="payment" value="'+a+'"/>')}})};