function reload(){var e=document.querySelectorAll(".checkbox");if(e.length>0)for(var c=0;c<e.length;c++){var d=e[c].childNodes[0];d.childNodes[0].hasAttribute("checked")&&e[c].classList.add("checkbox-checked")}}$(document).ready(function(){reload(),$(".checkbox label input").unbind().bind("click",function(){var e=$(this).parent().parent();0==$(this).prop("checked")?e.removeClass("checkbox-checked"):e.addClass("checkbox-checked"),reload()})});